// ðŸš€ Code Snapper - Enhanced macOS Demo
class CodeSnapper {
    private val highlighter = SyntaxHighlighter()

    fun generateImage(request: SnapRequest): ByteArray {
        val highlighted = highlighter.highlight(
            code = request.code,
            language = request.language,
            theme = request.theme
        )

        return ImageRenderer.create {
            title = "Code Snapper Demo"
            width = 1200
            height = 800
            backgroundTheme = "chatgpt5"
            font = "JetBrains Mono"
        }.render(highlighted)
    }

    suspend fun snap(call: ApplicationCall) {
        val request = call.receive<SnapRequest>()
        val imageBytes = generateImage(request)

        call.response.header(
            HttpHeaders.ContentDisposition,
            "attachment; filename=\"${request.filename}.png\""
        )

        call.respondBytes(
            bytes = imageBytes,
            contentType = ContentType.Image.PNG
        )
    }
}
